import React from 'react';
import { Field, reduxForm } from 'redux-form';

class StreamCreate extends React.Component {
    renderInput({input, label }) {
        return (
            <div className = "field">
            <label>{label}</label>
            <input{...input} />
            </div>
        )
    }

