    }

    render() {
        return (
            <form className="ui form" onSubmit={this.props.handleSubmit(this.onSubmit)}>
                <Field name="title" 
                component={this.renderInput} 
                label="Enter title"
                />
                <Field name="description" 
                component={this.renderInput}
                label="Enter Description" 
                />
            </form>
        )
    }
}

export default reduxForm({
    form: 'streamCreate'
})(StreamCreate);
